import Input from "Componentes/Input"
import styles from "./CadastrarEventos.module.css"
import { NavLink, useNavigate } from 'react-router-dom'
import { useState } from "react";
import axios from 'axios';


const api = axios.create({
  baseURL: 'http://localhost:8080'
});

const CadastrarEventos = () => {
    const [loading, setLoading] = useState()
    const [form, setForm] = useState([])
    const navigate = useNavigate()

    const onLogin = async (event) => {
        event.preventDefault();
        try {
            setLoading(true)
            // const responses = await axios.get('http://localhost:8080/relatorio_inscritos_por_atividade/1');
            // console.log('response do Login', responses)
            // const response = await UserService.login(form);
            const {response} = await axios.post('http://localhost:8080/login', form)

            console.log(response)

            if (response === true) {
              alert('evento cadastrado')
              navigate('/eventos')
            }
            setLoading(false)
          }
          catch (err) {
            alert('Algo deu errado com o Cadastro' + err)
          }
      };

    const HandleOnChange = (event) => {
        setForm({...form, [event.target.name]: event.target.value})
    }
    return (
        <form className={styles.formulario}>
            <div className={styles.options}>
                <h3>Status</h3>
                <select className={styles.comboBox} name="status" id="status">
                    <option value="ativo">Ativo</option>
                    <option value="inativo">Inativo</option>
                </select>
            </div>
            <Input 
                name='nome' 
                type="string" 
                required 
                placeholder="Insira o nome do evento"
                onChange={HandleOnChange} />

            <Input 
                name='descricao' 
                type="string" 
                required 
                placeholder="Insira a descrição do evento"
                onChange={HandleOnChange} />

            <Input 
                name='DataInicio' 
                type="date" 
                required 
                placeholder="Insira o nome do evento"
                onChange={HandleOnChange} />

            <Input 
                name='DataFinal' 
                type="date" 
                required 
                placeholder="Insira o nome do evento"
                onChange={HandleOnChange} />

            <Input 
                name='LocalID' 
                type="number" 
                required 
                placeholder="Insira o nome do evento"
                onChange={HandleOnChange} />

            <Input 
                name='Local' 
                type="string" 
                required 
                placeholder="Insira o local do evento"
                onChange={HandleOnChange} />

            <Input 
                name='HoraInicio' 
                type="time" 
                required 
                placeholder="Insira o nome do evento"
                onChange={HandleOnChange} />

            <Input 
                name='HoraFim' 
                type="time" 
                required 
                placeholder="Insira o nome do evento"
                onChange={HandleOnChange} />

            <button 
                type="submit"
                onClick={onLogin}
                className={styles.submit}
                text='Entrar'
                >Cadastrar
            </button>
        </form>
    )
}

export default CadastrarEventos
