                    <MenuLink to="/cadastro">
                        Cadastrar
                    </MenuLink>
                    <MenuLink to="/inscricao">
                        Inscrições
                    </MenuLink>
                    <MenuLink to="/listar">
                        Listar
                    </MenuLink>
                </nav>
            )}

            {taLogado && (
                <nav className={styles.navegacao}>
                    <button className={styles.link} onClick={logOut}>
                        Logout
                    </button>
                    <MenuLink to="/eventos">
                        Eventos
                    </MenuLink>
                </nav>
            )} 

            {!taLogado && (
                    <nav className={styles.navegacao}>
                    <MenuLink to="/login">
                        Login
                    </MenuLink>
                </nav>
            )}
            <MenuLinkNavegacao to="/">
                voltar
