import { useState } from "react";
import styles from "./BotaoPrincipal.module.css"
import { useNavigate } from "react-router-dom";
import axios from "axios";

const BotaoInscreverEmEvento = ({children, tamanho, IDUsuario, IDEvento}) => {
    const [formulario, setFormulario] = useState([])
    const navigate = useNavigate()
    const inscrever = (lugar) => {navigate(lugar)}

    async function getConteudo(links) {
        const url = links;
        let response = await axios.get(url);
        return response.data;
    }

    useEffect(() => {
        getConteudo("http://localhost:8080/tipoUsuario/").then((data) => setTipoUsuario(data))
        getConteudo("http://localhost:8080/instituicao/").then((data) => setiInstituicao(data))
    }, [])


    setFormulario({[EventoID]: IDEvento, [UsuarioID]: IDUsuario, [Status]: "ativo"})




    return (
        <button className={`
            ${styles.botaoPrincipal}
            ${styles[tamanho]}`}
            onClick={() => inscrever(`${destino}${IDDestino}`)}
            >
            {children}
        </button>
    )
}

export default BotaoInscreverEmEvento
