import styles from "./CadastrarAtividade.module.css"
import { useEffect, useState } from "react";
import axios from 'axios';

const api = axios.create({
  baseURL: 'http://localhost:8080'
});

const Eventos = () => {
  const [formulario, setFormulario] = useState([])
  const [loading, setLoading] = useState()

  const inscrever = async (ID) => {
    setFormulario({...formulario, "evento_id": ID})
    console.log(formulario)
    setFormulario({...formulario, "usuario_id": localStorage.getItem('idUsuario')})
    console.log(formulario)
    setFormulario({...formulario, "status": "ativo"})
    console.log(formulario)
    setFormulario({...formulario, "data": "22/01/2024"})
    console.log(formulario)
    setFormulario({...formulario, "hora": "08:08"})
    console.log(formulario)

    formulario.usuario_id = parseInt(formulario.usuario_id)
    try {
        setLoading(true)
        console.log(formulario)
        const {response} = await axios.post('http://localhost:8080/inscricaoEmEventos/', formulario).catch(function (error) {
            if (error.response) {
              window.alert(error.response.data.MENSAGEM);
              window.alert(error.response.data.error);
              console.log(error.response.status);
              console.log(error.response.headers);
            } else if (error.request) {
              console.log(error.request);
            } else {
              console.log('Error', error.message);
            }
        });

        window.alert(response.ID)

        if (response === undefined) {
          alert('cadastrado')
        }
    }

    catch (err) {
        alert('Algo deu errado com o Cadastro' + err)
    }

    finally {
    setLoading(false)
    setFormulario()
    }
  };
    const [conteudo, setConteudo] = useState([])

    async function getConteudo() {
        const url = "http://localhost:8080/evento/";
        let response = await axios.get(url);
        return response.data;
      }

    useEffect(() => {
        getConteudo().then((data) => setConteudo(data))
        console.log(conteudo)
    }, [])



    return (
      <div className={styles.formulario}>

        <ul>
            {conteudo.map(conteudo => (
                <li key={conteudo.ID}>
                    <h4>
                      <b>Status: </b>{conteudo.status}
                    </h4>
                    <h4>
                      Nome: {conteudo.nome}
                    </h4>
                    <h4>
                      Descrição: {conteudo.descricao}
                    </h4>
                    <h4>
                      Data inicio: {conteudo.data_inicio}
                    </h4>
                    <h4>
                      Data final: {conteudo.data_final}
                    </h4>
                    <h4>
                      Hora inicio: {conteudo.horaInicio}
                    </h4>
                    <h4>
                      Hora fim: {conteudo.horaFim}
                    </h4>   
                    <button 
                        type="submit"
                        className={styles.botaoPrincipal}
                        onClick={() => inscrever(conteudo.ID)}
                        >
                        Se Inscrever
                    </button>       
                </li>
            ))}
        </ul>

      </div>

    )
}

export default Eventos
