            {local}

            {ehADM && (
                <nav className={styles.navegacao}>
                    <MenuLink to="/cadastro">
                        Cadastrar
                    </MenuLink>
                    <MenuLink to="/inscricao">
                        Inscrições
                    </MenuLink>
                    <MenuLink to="/listar">
                        Listar
                    </MenuLink>
                </nav>
            )}

            {taLogado && (
                <nav className={styles.navegacao}>
                    <MenuLink to="/eventos">
                        Eventos
                    </MenuLink>
                </nav>
            )} 

            {taLogado && (
                <nav className={styles.navegacao}>
                    <button className={styles.link} onClick={logOut}>
                        Logout
                    </button>
                </nav>
            )}

            {!taLogado && (
                <nav className={styles.navegacao}>
                    <MenuLink to="/login">
                        Login
                    </MenuLink>
                </nav>
            )}
        </header>
    )





